import * as React from 'react';
import { useState, useEffect } from 'react';
import AppBar from '@mui/material/AppBar';
import Button from '@mui/material/Button';
import CameraIcon from '@mui/icons-material/PhotoCamera';
import Card from '@mui/material/Card';
import CardActions from '@mui/material/CardActions';
import CardContent from '@mui/material/CardContent';
import CardMedia from '@mui/material/CardMedia';
import CssBaseline from '@mui/material/CssBaseline';
import Grid from '@mui/material/Grid';
import Stack from '@mui/material/Stack';
import Box from '@mui/material/Box';
import Toolbar from '@mui/material/Toolbar';
import Typography from '@mui/material/Typography';
import Container from '@mui/material/Container';
import Link from '@mui/material/Link';
import { createTheme, ThemeProvider } from '@mui/material/styles';
import { getAllMovies } from '../Api/Movie/movie';
import axios from 'axios';
import { Table } from 'antd';
import Column from 'antd/lib/table/Column';

function Copyright() {
  return (
    <Typography variant="body2" color="text.secondary" align="center">
      {'Copyright © '}
      <Link color="inherit" href="https://mui.com/">
        MovieHub
      </Link>{' '}
      {new Date().getFullYear()}
      {'.'}
    </Typography>
  );
}

const cards = [1, 2, 3, 4, 5, 6, 7, 8, 9];

const theme = createTheme();

export default function Gallery() {
    const [movies, setMovies] = useState(null);
    const [requests, setRequests] = useState(null);

    async function getMovies() {
        console.log("nesgto")
        try {
          const response = await getAllMovies()
          console.log(response.data)
          setMovies(response.data)
        } catch (e) {
          console.error(e)
        }
      }

    /*  const getAllRequests = () => {
        return axios.get("http://localhost:8089/movie/api/movie/").then((response) => {
            setMovies(response.data);
            console.log(movies);
          }).catch(error => console.error(`${error}`));
        }
*/


const getAllRequests = () => {
    return axios.get("http://localhost:8080/api/request/", {
      headers: {
        'Authorization': 'Bearer eyJhbGciOiJIUzUxMiJ9.eyJzdWIiOiJhZG1pbiIsImlhdCI6MTY1NTE1OTQyNiwiZXhwIjoxNjU1MjQ1ODI2fQ.2QBgLU2ZbAWMTvZDEWoMaG0ry-aX0-wzMJhcWYaapVw0iySUtAKSzTjM33pwyLX9K0XC6DBAvM6869z9hfYMvg'
      }
    }).then((response) => {
        setRequests(response.data);
        console.log(requests);
      }).catch(error => console.error(`${error}`));
    }


  useEffect(() => {
    getAllRequests()
    async function fetchData() {
        console.log("nesgto")
        try {
          const response = await getAllMovies()
          console.log(response.data)
          setMovies(response.data)
        } catch (e) {
          console.error(e)
        }
    }
    fetchData()
  }, [])

  return (
  <><ThemeProvider theme={theme}>
              <CssBaseline />
              <main>
                  {/* Hero unit */}
                  <Box
                      sx={{
                          bgcolor: 'text.disabled',
                          pt: 8,
                          pb: 6,
                      }}
                  >
                      <Container maxWidth="sm">
                          <Typography
                              component="h1"
                              variant="h2"
                              align="center"
                              color="text.primary"
                              gutterBottom
                          >
                              Album layout
                          </Typography>
                          <Typography variant="h5" align="center" color="text.secondary" paragraph>
                              Something short and leading about the collection below—its contents,
                              the creator, etc. Make it short and sweet, but not too short so folks
                              don&apos;t simply skip over it entirely.
                          </Typography>
                          <Stack
                              sx={{ pt: 4 }}
                              direction="row"
                              spacing={2}
                              justifyContent="center"
                          >
                              <Button variant="contained">Main call to action</Button>
                              <Button variant="outlined">Secondary action</Button>
                          </Stack>
                      </Container>
                  </Box>
                  <Container sx={{ py: 8 }} maxWidth="md">
                      {/* End hero unit */}
                      <Grid container spacing={4}>
                          {movies.map((movie) => (
                              <Grid item key={movie} xs={12} sm={6} md={4}>
                                  <Card
                                      sx={{ height: '100%', display: 'flex', flexDirection: 'column' }}
                                  >
                                      <CardMedia
                                          component="img"
                                          sx={{
                                              // 16:9
                                              pt: '56.25%',
                                          }}
                                          image={movie.image}
                                          alt="random" />
                                      <CardContent sx={{ flexGrow: 1 }}>
                                          <Typography gutterBottom variant="h5" component="h2">
                                              {movie.title}
                                          </Typography>
                                          <Typography>
                                              {movie.description}
                                          </Typography>
                                      </CardContent>
                                      <CardActions>
                                          <Button size="small">View</Button>
                                          <Button size="small">Edit</Button>
                                      </CardActions>
                                  </Card>
                              </Grid>
                          ))}
                      </Grid>
                  </Container>
              </main>
              {/* Footer */}
              <Box sx={{ bgcolor: 'text.disabled', p: 6 }} component="footer">
                  <Copyright />
              </Box>
              {/* End footer */}
          </ThemeProvider></>
  );
}
